sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    console.log("hello")
    var listquery = api.state.listQuery;
    var listtitle = api.state.listTitle;

    var listqueryarray = listquery.split('^u_request_type=');

    var listtitlearray = listtitle.split(' - ');

    var passedQuery = event.payload.heListQuery;
    var passedTitle = event.payload.heListTitle;

    if (passedQuery == 'legal' || passedQuery == 'facilities' || passedQuery == 'hr') {
        api.setState('listQuery', listqueryarray[0] + '^u_request_type=' + passedQuery);
        api.setState('listTitle', listtitlearray[0] + ' - ' + passedQuery);
        console.log(listqueryarray[0] + "u_request_type=" + passedQuery);
    } else if (passedQuery == 'all') {
        api.setState('listQuery', listqueryarray[0]);
        api.setState('listTitle', listtitlearray[0]);
    } else {
        api.setState('listQuery', passedQuery);
        api.setState('listTitle', passedTitle);
    }
}
